const base_url="https://exchange-nodejs.herokuapp.com";document.querySelector("#login").addEventListener("click",()=>{var e=document.querySelector("#email").value,t=document.querySelector("#password").value;fetch(base_url+"/users/login",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:e,password:t})}).then(e=>e.json()).then(t=>{if("success"===t.status){var e=t.data.token,o=t.data.id;localStorage.setItem("token",e),localStorage.setItem("id",o),window.location.href="index.html"}else{let e=document.querySelector(".card__alert");e.textContent=t.message,e.classList.remove("hidden")}})});